import React, { useState } from 'react';

function App() {
  const [ingredients, setIngredients] = useState('');
  const [recipes, setRecipes] = useState([]);

  const handleSubmit = async () => {
    const response = await fetch('http://127.0.0.1:5000/recommend', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ingredients: ingredients.split(',').map(i => i.trim()) })
    });
    const data = await response.json();
    setRecipes(data);
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Recipe Recommender</h1>
      <input
        type="text"
        value={ingredients}
        onChange={e => setIngredients(e.target.value)}
        placeholder="Enter ingredients (comma-separated)"
        style={{ width: '300px', marginRight: '1rem' }}
      />
      <button onClick={handleSubmit}>Get Recipes</button>

      <div style={{ marginTop: '2rem' }}>
        {recipes.map((r, idx) => (
          <div key={idx} style={{ marginBottom: '1rem' }}>
            <h3>{r.name}</h3>
            <p><strong>Missing:</strong> {r.missing.join(', ') || 'None'}</p>
            <p><strong>Substitutes:</strong> {r.substitutes.join(', ') || 'None'}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
